module ApplicationHelper

  def display_base_errors resource
    return '' if (resource.errors.empty?) or (resource.errors[:base].empty?)
    messages = resource.errors[:base].map { |msg| content_tag(:p, msg) }.join
    html = <<-HTML
    <div class="alert alert-error alert-block">
      <button type="button" class="close" data-dismiss="alert">&#215;</button>
      #{messages}
    </div>
    HTML
    html.html_safe
  end
  
  def dummy_image_src
    "data:image/png;base64,"
  end
  
  def sort_class(column)
    params[:sort][:column] == column ? 'sort-value' : ''
  end
  
  def sort_link(text,sort_string)
    sort = {
      column: sort_string,
      order: (sort_string == params[:sort][:column] ? 'asc' : 'desc')
    }
    icon_class = ""
    link_class = ""
    if sort[:column] == params[:sort][:column]
      link_class = "selected"
      icon_class = "icon-angle-down"
      if params[:sort][:order] == 'asc'
        sort[:order] = 'desc'
        icon_class = "icon-angle-up"
      end
    end
    
    link_to("#{text} <i class='#{icon_class}'></i>".html_safe, {sort: params[:sort].merge(sort)}, class: link_class)
  end

end
